package solution;

import org.junit.jupiter.api.Test;
import solution.LongestPalindromicSubstring;

import static org.junit.jupiter.api.Assertions.*;

class LongestPalindromicSubstringTest {

    @Test
    void longestPalindrome() {
        LongestPalindromicSubstring palindromic = new LongestPalindromicSubstring();

        assertEquals("", palindromic.longestPalindrome(""));
        assertEquals("cbgbc", palindromic.longestPalindrome("bggbbcbgbcdgecdcfgbb"));
        assertEquals("bab", palindromic.longestPalindrome("babad"));

    }
}